import BandJS from '../band.js/dist/band';

export const Audio = (props) => {
	function playHandler() {
		let conductor = new BandJS();
		conductor.setTimeSignature(4, 4);
		conductor.setTempo(props.analysis.sections[0].tempo);
		let piano = conductor.createInstrument('sine');
		piano.note('quarter', 'A4');
		let player = conductor.finish();
		conductor.setOnDurationChangeCallback(() => {
			player.play();
		});
	}
	return <p onClick={playHandler}>click Me</p>;
};

export default Audio;
