export const TracksResults = React.forwardRef((props, ref) => {
	const trackRef = React.createRef();

	let getAnalysis = () => {
		axios
			.get(
				'https://api.spotify.com/v1/audio-analysis/' +
					this.trackRef.current.children[0].id,
				{
					headers: {
						Authorization: `${this.state.token_type} ${this.state.token}`
					}
				}
			)
			.then(data => {
				console.log(data.data);
			})
			.catch(err => {
				console.log(err);
			});
	};

	return (
		<ul ref={trackRef}>
			{props.tracks.map(track => {
				return (
					<li id={track.id} key={track.id} onClick={getAnalysis}>
						{track.name} - {track.artists[0].name} ({track.popularity})
					</li>
				);
			})}
		</ul>
	);
});

export default TracksResults;
