import React, { Fragmant, Component } from 'react';
//import { SpotifyApiContext, Search } from 'react-spotify-api';
import axios from 'axios';

/*class Rhythm extends Component {
	render() {
		return (
			<SpotifyApiContext.Provider value={`58b9c4063c904cda87af80186a732f01`}>
				<Search query={songName} track>
					{(data, loading, error) =>
						data ? (
							<ul>
								{data.tracks.items.map(song => {
									<li key={song.id}>{song.name}</li>;
								})}
							</ul>
						) : null
					}
				</Search>
			</SpotifyApiContext.Provider>
		);
	}
}

export default Rhythm;
*/

class Rhythm extends Component {
	constructor() {
		super();
		this.state = { token: null, tracks_list: [] };
	}

	componentDidMount() {
		const hash = window.location.hash
			.substring(1)
			.split('&')
			.reduce((initial, item) => {
				if (item) {
					var parts = item.split('=');
					initial[parts[0]] = decodeURIComponent(parts[1]);
				}
				return initial;
			}, {});
		this.setState({ token: hash.access_token, token_type: hash.token_type });
	}
	getTracks = () => {
		axios
			.get(
				'https://api.spotify.com/v1/search?q=never%20let%20you%20go&type=track',
				{
					headers: {
						Authorization: `${this.state.token_type} ${this.state.token}`
					}
				}
			)
			.then(data => {
				this.setState({ tracks_list: data.tracks.items });
			}).catch;
	};
	render() {
		return (
			<>
				{!this.state.token && (
					<button>
						<a href='https://accounts.spotify.com/authorize?client_id=58b9c4063c904cda87af80186a732f01&redirect_uri=http:%2F%2Flocalhost:3000&response_type=token'>
							Login With Spotify
						</a>
					</button>
				)}
			</>
		);
	}
}

export default Rhythm;
