import axios from 'axios';
import { SpotifyApiAxiosContext } from 'react-spotify-api';

class Rhythm extends Component {
	getSong = () => {
		spotifyApi.searchTracks('Never Let You Go').then(
			data => {
				console.log(data);
			},
			err => console.error(err)
		);
	};
	render() {
		return (
			<SpotifyApiAxiosContext.Provider value={axios}>
				<SpotifyApiContext.Provider value={token}>
					<p onClick={this.getSong()}>click here</p>{' '}
				</SpotifyApiContext.Provider>
			</SpotifyApiAxiosContext.Provider>
		);
	}
}

export default Rhythm;
