import BandJS from '../band.js/dist/band';

export const Audio = (props) => {
	function playHandler() {
		player.stop();
		let conductor = new BandJS();
		let sectionsProps = [];
		props.analysis.sections.forEach((section) => {
			sectionsProps.push([section.duration * 1000, section.tempo]);
		});
		let totalDuration = props.analysis.track.duration;
		console.log(sectionsProps);
		conductor.setTimeSignature(4, 4);
		conductor.setTempo(sectionsProps[0][1]);
		let piano = conductor.createInstrument('sine');
		piano.note('quarter', 'A4');
		let player = conductor.finish();
		player.loop(true);
		rhythmTimer(sectionsProps[0][1], sectionsProps[0][0]);
		function rhythmTimer(tempo, time) {
			player.play();
			sectionsProps.shift();
			setTimeout(() => {
				player.stop();
				if (sectionsProps[0][1]) {
					conductor.setTempo(sectionsProps[0][1]);
					player = conductor.finish();
					player.loop(true);
					rhythmTimer(sectionsProps[0][1], sectionsProps[0][0]);
				}
			}, time);
		}
	}
	return <p onClick={playHandler}>click Me</p>;
};

export default Audio;
